// Define "KssExampleEngine" as the name of our example template engine.
var KssExampleEngine,
	// Import the template engine object. We will use its API to scaffold our
	// template engine.
	KssTemplateEngine = require('kss/cli').KssTemplateEngine;


/**
 * Export our "KssExampleEngine" object.
 *
 * Our template engine is an instance of the KssTemplateEngine object with
 * additional functionality added by overriding the parent methods.
 */
module.exports = KssExampleEngine = new KssTemplateEngine();


/**
 * Set the template engine configuration.
 *
 * An instance of KssTemplateEngine MUST override this method and set the needed
 * template engine configuration. Currently, the "configuration" is limited to
 * just the API version, but future versions of kss-node will have further
 * requirements.
 */
KssExampleEngine.setConfiguration = function() {
	// Tell kss-node which version of the template engine API you are
	// implementing.
	this.API.engine = '2.1.x';

	// You can also check which version of the template engine API is implemented
	// in the currently-running kss-node by inspecting this.API.kss-node.
	if (this.API.kss-node == '2.0.x') {
		throw 'This version of KssExampleEngine is not compatible with your version of kss-node.';
	}
}

